  %

  this_block.setTopLevelLanguage('Verilog');

  this_block.setEntityName('atan2_top');

  % System Generator has to assume that your entity  has a combinational feed through; 
  %   if it  doesn't, then comment out the following line:
  this_block.tagAsCombinational;

  this_block.addSimulinkInport('rst');
  this_block.addSimulinkInport('addr_in');
  this_block.addSimulinkInport('eigen1_y');
  this_block.addSimulinkInport('eigen_x');
  this_block.addSimulinkInport('w_valid');

  this_block.addSimulinkOutport('addr_out');
  this_block.addSimulinkOutport('phase1');
  this_block.addSimulinkOutport('phase_valid');

  addr_out_port = this_block.port('addr_out');
  addr_out_port.setType('UFix_9_0');
  phase1_port = this_block.port('phase1');
  phase1_port.setType('UFix_16_0');
  phase_valid_port = this_block.port('phase_valid');
  phase_valid_port.setType('UFix_1_0');
  phase_valid_port.useHDLVector(false);

  % -----------------------------
  if (this_block.inputTypesKnown)
    % do input type checking, dynamic output type and generic setup in this code block.

    if (this_block.port('rst').width ~= 1);
      this_block.setError('Input data type for port "rst" must have width=1.');
    end

    this_block.port('rst').useHDLVector(false);

    if (this_block.port('addr_in').width ~= 9);
      this_block.setError('Input data type for port "addr_in" must have width=9.');
    end

    if (this_block.port('eigen1_y').width ~= 16);
      this_block.setError('Input data type for port "eigen1_y" must have width=16.');
    end

    if (this_block.port('eigen_x').width ~= 16);
      this_block.setError('Input data type for port "eigen_x" must have width=16.');
    end

    if (this_block.port('w_valid').width ~= 1);
      this_block.setError('Input data type for port "w_valid" must have width=1.');
    end

    this_block.port('w_valid').useHDLVector(false);

  end  % if(inputTypesKnown)
  % -----------------------------

  % -----------------------------
   if (this_block.inputRatesKnown)
     setup_as_single_rate(this_block,'clk','ce')
   end  % if(inputRatesKnown)
  % -----------------------------

    % (!) Set the inout port rate to be the same as the first input 
    %     rate. Change the following code if this is untrue.
    uniqueInputRates = unique(this_block.getInputRates);

  % (!) Custimize the following generic settings as appropriate. If any settings depend
  %      on input types, make the settings in the "inputTypesKnown" code block.
  %      The addGeneric function takes  3 parameters, generic name, type and constant value.
  %      Supported types are boolean, real, integer and string.
  this_block.addGeneric('DIN_WIDTH','integer','16');
  this_block.addGeneric('VECTOR_LEN','integer','512');
  this_block.addGeneric('ATAN_FILE','string','"/home/franco/seba/doa_test/vec_linalg2/init_mem/atan_rom.hex"');

  % Add addtional source files as needed.
  %  |-------------
  %  | Add files in the order in which they should be compiled.
